parameters.jsCode:
// Domain Randomizer for Dwary Intech Content Generation
// This ensures variety in business domains and prevents repetitive HR scenarios

const businessDomains = [
  {
    domain: "sales_automation",
    focus: "Sales pipeline automation, lead scoring, prospect outreach",
    audience: "Sales managers, business development teams, startup founders",
    keywords: "lead generation, sales pipeline, prospect qualification, CRM automation"
  },
  {
    domain: "ecommerce_automation", 
    focus: "Inventory management, order processing, customer analytics",
    audience: "E-commerce store owners, online retailers, dropshipping businesses",
    keywords: "inventory forecasting, order automation, customer segmentation, product recommendations"
  },
  {
    domain: "marketing_automation",
    focus: "Content creation, campaign management, social media scheduling", 
    audience: "Marketing managers, content creators, digital agencies",
    keywords: "content generation, campaign optimization, social media automation, email marketing"
  },
  {
    domain: "customer_service",
    focus: "Support ticket automation, chatbot workflows, customer onboarding",
    audience: "Customer success managers, support teams, SaaS companies", 
    keywords: "support automation, customer onboarding, helpdesk workflows, response automation"
  },
  {
    domain: "finance_operations",
    focus: "Invoice processing, expense tracking, financial reporting",
    audience: "Finance managers, accountants, small business owners",
    keywords: "invoice automation, expense reporting, financial dashboards, payment processing"
  },
  {
    domain: "operations_optimization",
    focus: "Workflow automation, task management, process optimization",
    audience: "Operations managers, project managers, team leads",
    keywords: "workflow optimization, task automation, process efficiency, team coordination"
  },
  {
    domain: "real_estate",
    focus: "Property management, lead qualification, document processing",
    audience: "Real estate agents, property managers, real estate agencies", 
    keywords: "property automation, client management, document processing, market analysis"
  },
  {
    domain: "consulting_services",
    focus: "Client onboarding, project management, report generation",
    audience: "Consultants, professional services, agencies",
    keywords: "client automation, project workflows, report generation, service delivery"
  }
];

// Get random domain (different from last few runs if possible)
const randomIndex = Math.floor(Math.random() * businessDomains.length);
const selectedDomain = businessDomains[randomIndex];

// Create instruction for AI agent
const domainInstruction = `
DOMAIN FOCUS: ${selectedDomain.domain}
TARGET AUDIENCE: ${selectedDomain.audience}
AUTOMATION FOCUS: ${selectedDomain.focus}
KEYWORDS TO USE: ${selectedDomain.keywords}

STRICT RULES:
- Do NOT generate HR, recruiting, resume screening, or job-related content
- Focus ONLY on ${selectedDomain.domain} scenarios
- Use business contexts relevant to ${selectedDomain.audience}
- Incorporate keywords: ${selectedDomain.keywords}
`;

// Add variety in scenario types
const scenarioTypes = [
  "business meeting presentation",
  "founder testimonial interview", 
  "peer recommendation conversation",
  "conference demo presentation",
  "client success story call",
  "team collaboration session"
];

const randomScenario = scenarioTypes[Math.floor(Math.random() * scenarioTypes.length)];

// Generate timestamp for uniqueness
const timestamp = new Date().toISOString();

return {
  selectedDomain: selectedDomain.domain,
  domainFocus: selectedDomain.focus,
  targetAudience: selectedDomain.audience,
  scenarioType: randomScenario,
  instruction: domainInstruction,
  avoidTopics: "HR, recruiting, resume screening, job posting, candidate management, hiring processes",
  timestamp: timestamp,
  variety_seed: Math.floor(Math.random() * 1000)
};
