parameters.jsCode:
// This object will hold our combined data.
const consolidatedData = {};

// Loop through the incoming items from the Merge node.
for (const item of items) {
  // Check if this item has the job description ('output')
  if (item.json.output) {
    consolidatedData.jobDescription = item.json.output;
  }

  // Check if this item has the 'mustHave' string
  if (item.json.mustHave) {
    consolidatedData.mustHaves = item.json.mustHave;
  }

  // Check if this item has the candidate profile ('response')
  if (item.json.response && item.json.response.text) {
    consolidatedData.candidateProfile = item.json.response.text;
  }

  // Check for Candidate ID
  if (item.json["CandidateID"]) {
    consolidatedData.candidateId = item.json["CandidateID"];
  }

  // Check for joinTime
  if (item.json["joinTime"]) {
    consolidatedData.joinTime = item.json["joinTime"];
  }

  // Check for hiringUrgency - Corrected to save to its own field
  if (item.json["hiringUrgency"]) {
    consolidatedData.hiringUrgency = item.json["hiringUrgency"];
  }
} // The for-loop ends here.

// Return a new array with a single item containing all the data.
return [{
  json: consolidatedData
}];
