// Instaloader Content Processing Node
// Processes scraped Instagram data from the JSON file

const webhookData = $input.first().json;
const posts = webhookData.posts || [];

// Sort posts by engagement (likes + comments)
const sortedPosts = posts.sort((a, b) => {
  const engagementA = (a.likes || 0) + (a.comments || 0);
  const engagementB = (b.likes || 0) + (b.comments || 0);
  return engagementB - engagementA;
});

// Select top performing posts for analysis
const topPosts = sortedPosts.slice(0, 5);

// Extract content patterns
const contentAnalysis = {
  totalPosts: posts.length,
  averageEngagement: posts.reduce((sum, post) => sum + (post.likes + post.comments), 0) / posts.length,
  topHashtags: extractTopHashtags(posts),
  bestPerformingPost: topPosts[0],
  contentPatterns: analyzeContentPatterns(topPosts),
  postingTimes: posts.map(post => new Date(post.date).getHours()),
  selectedPosts: topPosts
};

function extractTopHashtags(posts) {
  const hashtagCount = {};
  posts.forEach(post => {
    if (post.hashtags) {
      post.hashtags.forEach(tag => {
        hashtagCount[tag] = (hashtagCount[tag] || 0) + 1;
      });
    }
  });

  return Object.entries(hashtagCount)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 10)
    .map(([tag]) => tag);
}

function analyzeContentPatterns(posts) {
  const patterns = {
    averageCaptionLength: 0,
    commonWords: {},
    emotionTone: 'neutral',
    hasCallToAction: false
  };

  let totalLength = 0;
  posts.forEach(post => {
    if (post.caption) {
      totalLength += post.caption.length;
      const words = post.caption.toLowerCase().match(/\b\w+\b/g) || [];
      words.forEach(word => {
        if (word.length > 3) {
          patterns.commonWords[word] = (patterns.commonWords[word] || 0) + 1;
        }
      });
      if (post.caption.includes('shop') || post.caption.includes('buy') || post.caption.includes('link')) {
        patterns.hasCallToAction = true;
      }
    }
  });

  patterns.averageCaptionLength = Math.round(totalLength / posts.length);

  return patterns;
}

return [{
  json: {
    originalData: webhookData,
    analysis: contentAnalysis,
    readyForAI: true,
    timestamp: new Date().toISOString()
  }
}];

// n8n Function: Filter out already processed posts.
const analysisResult = $input.first().json;
const topPosts = analysisResult.analysis.selectedPosts;

// Get the list of already processed post IDs from your Google Sheet.
const processedItems = $items("get-post-history");
const processedPostIds = new Set(processedItems.map(item => item.json['Post ID']));

// Filter the top posts, keeping only the ones that have NOT been processed before.
const newTopPosts = topPosts.filter(post => !processedPostIds.has(post.shortcode));

// If there are no new posts to process, stop the workflow.
if (newTopPosts.length === 0) {
  console.log("No new high-performing posts to process. Stopping workflow.");
  return null; // Stops the workflow from proceeding.
}

console.log(`Found ${newTopPosts.length} new posts to process.`);

// Replace the old list of posts with our newly filtered list.
analysisResult.analysis.selectedPosts = newTopPosts;

// Pass the updated data to the next node (AI Content Variation Generator).
return [{
  json: analysisResult
}];

