// Content Parsing and Platform Optimization
// Parses AI-generated content and prepares for each platform

const aiResponse = $input.first().json.choices[0].message.content;
const originalData = $input.first().json.analysis;

// Parse AI response to extract platform-specific content
const platforms = {
  instagram: extractPlatformContent(aiResponse, 'Instagram'),
  facebook: extractPlatformContent(aiResponse, 'Facebook'), 
  linkedin: extractPlatformContent(aiResponse, 'LinkedIn')
};

function extractPlatformContent(text, platform) {
  const regex = new RegExp(`${platform}[\s\S]*?(?=\n\n|$)`, 'i');
  const match = text.match(regex);

  if (match) {
    const content = match[0].replace(new RegExp(`^.*?${platform}.*?\n`, 'i'), '').trim();
    return {
      caption: content,
      hashtags: extractHashtags(content),
      platform: platform.toLowerCase(),
      ready: true
    };
  }

  return {
    caption: `Check out our latest ${originalData.selectedPosts[0].is_video ? 'video' : 'collection'}! üî•`,
    hashtags: originalData.topHashtags.slice(0, 5),
    platform: platform.toLowerCase(),
    ready: false
  };
}

function extractHashtags(text) {
  const hashtagRegex = /#\w+/g;
  const matches = text.match(hashtagRegex) || [];
  return matches.map(tag => tag.substring(1)); // Remove # symbol
}

// Prepare scheduling data
const schedulingData = {
  baseContent: {
    imageUrl: originalData.selectedPosts[0].url,
    originalEngagement: originalData.selectedPosts[0].likes + originalData.selectedPosts[0].comments,
    contentType: originalData.selectedPosts[0].is_video ? 'video' : 'image'
  },
  platforms: platforms,
  metadata: {
    generatedAt: new Date().toISOString(),
    originalPost: originalData.selectedPosts[0].shortcode,
    performanceScore: calculatePerformanceScore(originalData.selectedPosts[0])
  }
};

function calculatePerformanceScore(post) {
  const engagement = post.likes + post.comments;
  const daysSincePost = Math.floor((new Date() - new Date(post.date)) / (1000 * 60 * 60 * 24));
  return Math.round(engagement / Math.max(daysSincePost, 1));
}

return [{
  json: schedulingData
}];

// Success Notification Generator
// Creates summary of automation execution

const executionData = $input.first().json;
const timestamp = new Date().toISOString();

const summary = {
  status: 'SUCCESS',
  execution_time: timestamp,
  original_post: executionData.metadata?.originalPost || 'unknown',
  platforms_posted: {
    instagram: executionData.platforms?.instagram?.ready || false,
    facebook: executionData.platforms?.facebook?.ready || false,
    linkedin: executionData.platforms?.linkedin?.ready || false
  },
  performance_metrics: {
    original_engagement: executionData.baseContent?.originalEngagement || 0,
    performance_score: executionData.metadata?.performanceScore || 0,
    content_type: executionData.baseContent?.contentType || 'image'
  },
  next_execution: new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString()
};

// Generate notification message
const message = `üöÄ Content Automation Completed Successfully!\n\nüìä Execution Summary:\n‚Ä¢ Original Post: ${summary.original_post}\n‚Ä¢ Performance Score: ${summary.performance_metrics.performance_score}\n‚Ä¢ Platforms Posted: ${Object.entries(summary.platforms_posted).filter(([,posted]) => posted).map(([platform]) => platform).join(', ')}\n‚Ä¢ Content Type: ${summary.performance_metrics.content_type}\n\n‚è∞ Next Execution: ${new Date(summary.next_execution).toLocaleString()}\n\n‚úÖ All systems running smoothly!`;

return [{
  json: {
    summary: summary,
    notification_message: message,
    webhook_response: {
      status: 'success',
      message: 'Content automation completed',
      execution_id: `exec_${Date.now()}`,
      timestamp: timestamp
    }
  }
}];
